INSERT INTO SPEAKER(FIRST_NAME, LAST_NAME, TWITTER, GITHUB, BIO) values ('Dave', 'Syer', 'david_syer', 'dsyer', 'Founder and contributor to Spring Batch, lead of Spring Security OAuth, and an active contributor to Spring Integration, Spring Framework, Spring AMQP, Spring Security. Experienced, delivery-focused architect and development manager. Has designed and built successful enterprise software solutions using Spring, and implemented them in major institutions worldwide.');
INSERT INTO SPEAKER(FIRST_NAME, LAST_NAME, TWITTER, GITHUB, BIO) values ('Andy', 'Wilkinson', 'ankinson', 'wilkinsona', 'Husband. Dad. Fair weather mountain biker. Developer at Pivotal/SpringSource by day, iOS developer @deftmethods by night.');
INSERT INTO SPEAKER(FIRST_NAME, LAST_NAME, TWITTER, GITHUB, BIO) values ('Phil', 'Webb', 'phillip_webb', 'philwebb', 'Phil Webb is a Spring Framework developer and co-creator of the Spring Boot project. Prior to joining Pivotal and relocating to California, Phil worked for a number of UK technology companies.');
